'use strict';

var fs   = require('fs'),
    path = require('path');

var rules = {},
    ruleDir = path.join(__dirname, 'rules');

fs.readdirSync(ruleDir).forEach(function(key) {
    var match = key.match(/([^\\\/]+)\.js$/i);
    if (match) {
        rules[match[1]] = require(path.join(ruleDir, key));
    }
});

module.exports = {
    rules: rules,
    env: {
        browser: true,
        amd: true,
        jasmine: true,
        node: true
    },
    globals: {
        baidu: true,
        bds: true,
        T: true,
        $: true,
        jQuery: true,
        goog: true,
        fis: true,
        F: true,
        __inline: true,
        __uri: true,
        angular: true,
        setImmediate: true,
        ActiveXObject: true,
        unescape: true,
        escape: true,
        Promise: true
    }
};
